//El producto más grande de una serie
//Los cuatro dígitos adyacentes, en los 1000 digitos, con mayor producto son 9x9x8x9=5832
//Encuentra los trece dígitos adyacentes, en los 1000 digitos, que tienen el mayor producto. 
//¿Cuál es el valor de este producto?


#include <iostream>
using namespace std;

int main(){
	
	int n=13;
	string numero="";
	int tamanio=numero.length();
	int arr[tamanio];
	for(int i=0; i<tamanio; i++){
		arr[i]=numero[i] - '0';
	}
	
	long long productoMayor=0;
	for(int i=0; i<tamanio; i++){
		long long producto=1;
		for(int j=0; j<n; j++){
			producto*=arr[i+j];
		}
		if(productoMayor<producto){
			productoMayor=producto;
		}
	}
	cout << productoMayor << endl;
	return 0;
}
